import "./styles.css";
import "leaflet/dist/leaflet.css";
import { useState, useRef, useMemo, useCallback, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";

import { Icon, divIcon, point } from "leaflet";





import axios from 'axios';



// create custom icon
const customIcon = new Icon({
    iconUrl: require("./img/google-maps.png"),
    iconSize: [38, 38] // size of the icon
});

// custom cluster icon
const createClusterCustomIcon = function (cluster) {
    return new divIcon({
        html: `<span class="cluster-icon">${cluster.getChildCount()}</span>`,
        className: "custom-marker-cluster",
        iconSize: point(33, 33, true)
    });
};

// markers
// const markers = [
//     {
//         geocode: [48.86, 2.3522],
//         popUp: "Hello, I am pop up 1"
//     },
//     {
//         geocode: [48.85, 2.3522],
//         popUp: "Hello, I am pop up 2"
//     },
//     {
//         geocode: [48.855, 2.34],
//         popUp: "Hello, I am pop up 3"
//     }
// ];


const center = [31.951591711679697, 35.93934368582822]
const zoom = 19

function DisplayPosition({ map }) {
    const [position, setPosition] = useState(() => map.getCenter())

    const onClick = useCallback(() => {
        map.setView(center, zoom)
    }, [map])

    const onMove = useCallback(() => {
        setPosition(map.getCenter())
    }, [map])

    useEffect(() => {
        map.on('move', onMove)
        return () => {
            map.off('move', onMove)
        }
    }, [map, onMove])

    return (
        <p>
            latitude: {position.lat.toFixed(4)}, longitude: {position.lng.toFixed(4)}{' '}
            <button onClick={onClick}>reset</button>
        </p>
    )
}

export default function App1() {


    const [data, setData] = useState([]);
    const [markers, setMarkers] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:3001/maps');
                setData(response.data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, [data]);

    useEffect(() => {
        const newMarkers = data.map((item) => ({
            geocode: [item.LAT, item.LNG],
            popUp: `${data.Notes}`,
        }));
        setMarkers(newMarkers);
    }, [data]);

    const customIcon = new Icon({
        iconUrl: require("./img/google-maps.png"),
        iconSize: [38, 38],
    });



    const [map, setMap] = useState(null)

    const displayMap = useMemo(
        () => (
            <MapContainer
                center={center}
                zoom={zoom}
                // scrollWheelZoom={false}
                ref={setMap}>
                <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                <MarkerClusterGroup>
                    {markers.map((marker, index) => (
                        <Marker key={index} position={marker.geocode} icon={customIcon}   >
                            {/* <Popup>{marker.popUp}</Popup> */}
                            <Popup>'hallo'</Popup>
                        </Marker>
                    ))}
                </MarkerClusterGroup>
                <div className='mapdot'>.

                </div>
            </MapContainer>
        ),
        [],
    )

    // =============================


    const [dataa, setDataa] = useState([]);
    const [formDataa, setFormData] = useState({
        Name: '',
        Notes: '',
        LAT: '',
        LNG: '',
    });
    const [updatingId, setUpdatingId] = useState(null);
    const [updatedFormDataa, setUpdatedFormData] = useState({
        Name: '',
        Notes: '',
        LAT: '',
        LNG: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (updatingId !== null) {
            setUpdatedFormData({ ...updatedFormData, [name]: value });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const fetchData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/maps');
            setData(response.data);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const deleteItem = async (itemId) => {
        try {
            await axios.delete(`http://localhost:3001/maps/${itemId}`);
            fetchData(); // Fetch and update the data after a successful deletion
        } catch (error) {
            console.error('Error deleting item:', error);
        }
    };

    const updateItem = async () => {
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
            };
            await axios.patch(`http://localhost:3001/maps/${updatingId}`, updatedFormData, config);
            fetchData(); // Fetch and update the data after a successful update
            setUpdatingId(null); // Clear the updating state
        } catch (error) {
            console.error('Error updating item:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
            };
            await axios.post('http://localhost:3001/maps', formData, config);
            fetchData(); // Fetch and update the data after a successful submission
        } catch (error) {
            console.error('Error:', error);
        }
    };

    useEffect(() => {
        fetchData(); // Fetch initial data when the component mounts
    }, []);

    // =============================

    return (

        <>
        <div className="suctionMap">
            {map ? <DisplayPosition map={map} /> : null}
            {displayMap}
        </div>
         <div>
            <h1>Data Form</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="Name">Name:</label>
                    <input
                        type="text"
                        id="Name"
                        name="Name"
                        value={formData.Name}
                        onChange={handleChange}
                    />
                </div>

                <div>
                    <label htmlFor="Notes">Notes:</label>
                    <textarea
                        id="Notes"
                        name="Notes"
                        value={formData.Notes}
                        onChange={handleChange}
                    />
                </div>

                <div>
                    <label htmlFor="LAT">LAT:</label>
                    <input
                        type="text"
                        id="LAT"
                        name="LAT"
                        value={formData.LAT}
                        onChange={handleChange}
                    />
                </div>

                <div>
                    <label htmlFor="LNG">LNG:</label>
                    <input
                        type="text"
                        id="LNG"
                        name="LNG"
                        value={formData.LNG}
                        onChange={handleChange}
                    />
                </div>

                <button type="submit">Submit</button>
            </form>

            <div>
                <h1>Data Table</h1>
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Notes</th>
                            <th>LAT</th>
                            <th>LNG</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((item) => (
                            <tr key={item.ID}>
                                <td>{item.ID}</td>
                                <td>{item.Name}</td>
                                <td>{item.Notes}</td>
                                <td>{item.LAT}</td>
                                <td>{item.LNG}</td>
                                <td>
                                    <button onClick={() => deleteItem(item.ID)}>Delete</button>
                                    <button onClick={() => setUpdatingId(item.ID)}>Update</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {updatingId !== null && (
                <div>
                    <h1>Update Record</h1>
                    <form onSubmit={updateItem}>
                        <div>
                            <label htmlFor="Name">Name:</label>
                            <input
                                type="text"
                                id="Name"
                                name="Name"
                                value={updatedFormData.Name}
                                onChange={handleChange}
                            />
                        </div>

                        <div>
                            <label htmlFor="Notes">Notes:</label>
                            <textarea
                                id="Notes"
                                name="Notes"
                                value={updatedFormData.Notes}
                                onChange={handleChange}
                            />
                        </div>

                        <div>
                            <label htmlFor="LAT">LAT:</label>
                            <input
                                type="text"
                                id="LAT"
                                name="LAT"
                                value={updatedFormData.LAT}
                                onChange={handleChange}
                            />
                        </div>

                        <div>
                            <label htmlFor="LNG">LNG:</label>
                            <input
                                type="text"
                                id="LNG"
                                name="LNG"
                                value={updatedFormData.LNG}
                                onChange={handleChange}
                            />
                        </div>

                        <button type="submit">Update</button>
                    </form>
                </div>
            )}
        </div>
        </>

    );
}
