import React, { useState, useEffect } from 'react';
import './styles.css';
import "leaflet/dist/leaflet.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { Icon } from "leaflet";
import MarkerClusterGroup from 'react-leaflet-cluster';
import axios from 'axios';

export default function App() {
  const [data, setData] = useState([]);
  const [markers, setMarkers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3001/maps');
        setData(response.data);
      } catch (error) {
        console.error('Error:', error);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    // Update markers when data changes
    const newMarkers = data.map((item) => ({
      key: item.id, // Use a unique key for each marker
      position: [item.LAT, item.LNG],
      popUp: "Hello, I am pop up 1",
    }));
    setMarkers(newMarkers);
  }, [data]);

  return (
    <MapContainer center={[48.8566, 2.3522]} zoom={13} style={{ height: "100vh" }}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
      />
      <MarkerClusterGroup>
        {markers.map((marker) => (
          <Marker key={marker.key} position={marker.position}>
            <Popup>{marker.popUp}</Popup>
          </Marker>
        ))}
      </MarkerClusterGroup>
    </MapContainer>
  );
}
