// FormComponent.js
import React, { useState } from 'react';
import axios from 'axios';

const Form = () => {
    const [formData, setFormData] = useState({
        Name: '',
        Notes: '',
        LAT: '',
        LNG: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json', // Set the Content-Type header to application/json
                },
            };
            const response = await axios.post('http://localhost:3001/maps', formData, config);

            // const response = await axios.post('https://64b9313b79b7c9def6c0bc99.mockapi.io/CRUD', formData);
            console.log('Response:', response.data);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <>
        <form onSubmit={handleSubmit}>
            <div>
                <label htmlFor="Name">Name:</label>
                <input
                    type="text"
                    id="Name"
                    name="Name"
                    value={formData.Name}
                    onChange={handleChange}
                />
            </div>

            <div>
                <label htmlFor="Notes">Notes:</label>
                <textarea
                    id="Notes"
                    name="Notes"
                    value={formData.Notes}
                    onChange={handleChange}
                />
            </div>

            <div>
                <label htmlFor="lat">LAT:</label>
                <input
                    type="text"
                    id="LAT"
                    name="LAT"
                    value={formData.LAT}
                    onChange={handleChange}
                />
            </div>

            <div>
                <label htmlFor="LNG">LNG:</label>
                <input
                    type="text"
                    id="LNG"
                    name="LNG"
                    value={formData.LNG}
                    onChange={handleChange}
                />
            </div>

            <button type="submit" name="submit">Submit</button>
        </form>

        {/* <div>
      <h1>Data Table</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Notes</th>
            <th>LAT</th>
            <th>LNG</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.Name}</td>
              <td>{item.Notes}</td>
              <td>{item.LAT}</td>
              <td>{item.LNG}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div> */}
    </>
    );
};

export default Form;
