import "./styles.css";
import "leaflet/dist/leaflet.css";
import { useState, useRef, useMemo, useCallback, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";
import Form from './Form';
import { Icon } from "leaflet";
import axios from 'axios';

const center = [31.951591711679697, 35.93934368582822];
const zoom = 19;

export default function MapWithMarkers() {
    const [data, setData] = useState([]);
    const [markers, setMarkers] = useState([]);
    const [position, setPosition] = useState(center);
    const mapRef = useRef();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:3001/maps');
                setData(response.data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, []); // Only run once when the component mounts

    useEffect(() => {
        const newMarkers = data.map((item) => ({
            geocode: [item.LAT, item.LNG],
            popUp: item.Notes, // Use item.Notes for popup content
        }));
        setMarkers(newMarkers);
    }, [data]);

    const customIcon = new Icon({
        iconUrl: require("./img/google-maps.png"),
        iconSize: [38, 38],
    });

    const handleResetClick = () => {
        mapRef.current.setView(center, zoom);
    };

    const handleMapMove = () => {
        const currentMap = mapRef.current;
        if (currentMap) {
            setPosition(currentMap.getCenter());
        }
    };

    return (
        <div className="suctionMap">
            <MapContainer
                center={center}
                zoom={zoom}
                ref={mapRef}
                onMove={handleMapMove}
            >
                <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                <MarkerClusterGroup>
                    {markers.map((marker, index) => (
                        <Marker key={index} position={marker.geocode} icon={customIcon}>
                            <Popup>{marker.popUp}</Popup>
                        </Marker>
                    ))}
                </MarkerClusterGroup>
            </MapContainer>
            {position && (
                <p>
                    Latitude: {position.lat.toFixed(4)}, Longitude: {position.lng.toFixed(4)}{' '}
                    <button onClick={handleResetClick}>Reset</button>
                </p>
            )}
            <Form lat={position.lat.toFixed(4)} long={position.lng.toFixed(4)} />
        </div>
    );
}
