import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Form = (props) => {
    const [data, setData] = useState([]);
    const [formData, setFormData] = useState({
        Name: '',
        Notes: '',
        LAT: '',
        LNG: '',
    });
    const [updatingId, setUpdatingId] = useState(null);
    const [updatedFormData, setUpdatedFormData] = useState({
        Name: '',
        Notes: '',
        LAT: '',
        LNG: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (updatingId !== null) {
            setUpdatedFormData({ ...updatedFormData, [name]: value });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const fetchData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/maps');
            setData(response.data);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const deleteItem = async (itemId) => {
        try {
            await axios.delete(`http://localhost:3001/maps/${itemId}`);
            fetchData(); // Fetch and update the data after a successful deletion
        } catch (error) {
            console.error('Error deleting item:', error);
        }
    };

    const updateItem = async () => {
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
            };
            await axios.patch(`http://localhost:3001/maps/${updatingId}`, updatedFormData, config);
            fetchData(); // Fetch and update the data after a successful update
            setUpdatingId(null); // Clear the updating state
        } catch (error) {
            console.error('Error updating item:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
            };
            await axios.post('http://localhost:3001/maps', formData, config);
            fetchData(); // Fetch and update the data after a successful submission
        } catch (error) {
            console.error('Error:', error);
        }
    };

    useEffect(() => {
        fetchData(); // Fetch initial data when the component mounts
    }, []);

    return (
        <div>
     
            <h1>Data Form</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="Name">Name:</label>
                    <input
                        type="text"
                        id="Name"
                        name="Name"
                        value={formData.Name}
                        onChange={handleChange}
                    />
                </div>

                <div>
                    <label htmlFor="Notes">Notes:</label>
                    <textarea
                        id="Notes"
                        name="Notes"
                        value={formData.Notes}
                        onChange={handleChange}
                    />
                </div>

                <div>
                    <label htmlFor="LAT">LAT:</label>
                    <input
                        type="text"
                        id="LAT"
                        name="LAT"
                        value={formData.LAT }
                        onChange={handleChange}
                    />
                </div>

                <div>
                    <label htmlFor="LNG">LNG:</label>
                    <input
                        type="text"
                        id="LNG"
                        name="LNG"
                        value={formData.LNG }
                        onChange={handleChange}
                    />
                </div>

                <button type="submit">Submit</button>
            </form>

            <div>
                <h1>Data Table</h1>
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Notes</th>
                            <th>LAT</th>
                            <th>LNG</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((item) => (
                            <tr key={item.ID}>
                                <td>{item.ID}</td>
                                <td>{item.Name}</td>
                                <td>{item.Notes}</td>
                                <td>{item.LAT}</td>
                                <td>{item.LNG}</td>
                                <td>
                                    <button onClick={() => deleteItem(item.ID)}>Delete</button>
                                    <button onClick={() => setUpdatingId(item.ID)}>Update</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {updatingId !== null && (
                <div>
                    <h1>Update Record</h1>
                    <form onSubmit={updateItem}>
                        <div>
                            <label htmlFor="Name">Name:</label>
                            <input
                                type="text"
                                id="Name"
                                name="Name"
                                value={updatedFormData.Name}
                                onChange={handleChange}
                            />
                        </div>

                        <div>
                            <label htmlFor="Notes">Notes:</label>
                            <textarea
                                id="Notes"
                                name="Notes"
                                value={updatedFormData.Notes}
                                onChange={handleChange}
                            />
                        </div>

                        <div>
                            <label htmlFor="LAT">LAT:</label>
                            <input
                                type="text"
                                id="LAT"
                                name="LAT"
                                value={updatedFormData.LAT}
                                onChange={handleChange}
                            />
                        </div>

                        <div>
                            <label htmlFor="LNG">LNG:</label>
                            <input
                                type="text"
                                id="LNG"
                                name="LNG"
                                value={updatedFormData.LNG}
                                onChange={handleChange}
                            />
                        </div>

                        <button type="submit">Update</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default Form;
