import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Form = () => {
    const [data, setData] = useState([]);
    const [formData, setFormData] = useState({
        Name: '',
        Notes: '',
        LAT: '',
        LNG: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const fetchData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/maps');
            setData(response.data);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const deleteItem = async (itemId) => {
        try {
            await axios.delete(`http://localhost:3001/maps/${itemId}`);
            fetchData(); // Fetch and update the data after a successful deletion
        } catch (error) {
            console.error('Error deleting item:', error);
        }
    };

    useEffect(() => {
        fetchData(); // Fetch initial data when the component mounts
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
            };
            await axios.post('http://localhost:3001/maps', formData, config);
            fetchData(); // Fetch and update the data after a successful submission
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div>
            <h1>Data Form</h1>
            <form onSubmit={handleSubmit}>
                {/* ... (form input fields) */}
                <button type="submit">Submit</button>
            </form>

            <div>
                <h1>Data Table</h1>
                <table>
                    <thead>
                        {/* ... (table headers) */}
                    </thead>
                    <tbody>
                        {data.map((item) => (
                            <tr key={item.ID}>
                                <td>{item.ID}</td>
                                <td>{item.Name}</td>
                                <td>{item.Notes}</td>
                                <td>{item.LAT}</td>
                                <td>{item.LNG}</td>
                                <td><button onClick={() => deleteItem(item.ID)}>Delete</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Form;
