import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Form = () => {
    const [items, setItems] = useState([]);
    const [error, setError] = useState(null);
    
    const [data, setData] = useState([]);
    const [formData, setFormData] = useState({
        Name: '',
        Notes: '',
        LAT: '',
        LNG: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const fetchData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/maps');
            setData(response.data);
        } catch (error) {
            console.error('Error:', error);
        }

        const deleteItem = async (itemId) => {
            try {
                await axios.delete(`http://localhost:3001/maps/${itemId}`);
                setItems((prevItems) => prevItems.filter((item) => item.id !== itemId));
            } catch (error) {
                setError('Error deleting item: ' + error.message);
            }
        };
    };

    useEffect(() => {
        fetchData(); // Fetch initial data when the component mounts
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
            };
            await axios.post('http://localhost:3001/maps', formData, config);
            fetchData(); // Fetch and update the data after a successful submission
        } catch (error) {
            console.error('Error:', error);
        }

        //========del====================
        
            // const [items, setItems] = useState([]); // Initialize items with an empty array

            // // Your deleteItem function
            // const deleteItem = async (itemId) => {
            //     try {
            //         const response = await axios.delete(`http://localhost:3001/maps/${itemId}`);
            //         return response.data;
            //     } catch (error) {
            //         console.error('Error deleting item:', error);
            //         throw error; // Rethrow the error for error handling further up the call stack
            //     }
            // };

            // // Function to handle item deletion
            // const handleDelete = async (itemId) => {
            //     try {
            //         await deleteItem(itemId); // Delete the item
            //         // Filter the items to remove the deleted item from the state
            //         setItems((prevItems) => prevItems.filter((item) => item.id !== itemId));
            //     } catch (error) {
            //         // Handle the error here, e.g., display an error message to the user
            //     }
            // };
        //========del====================

    };

    return (
        <div>
            <h1>Data Form</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="Name">Name:</label>
                    <input
                        type="text"
                        id="Name"
                        name="Name"
                        value={formData.Name}
                        onChange={handleChange}
                    />
                </div>

                <div>
                    <label htmlFor="Notes">Notes:</label>
                    <textarea
                        id="Notes"
                        name="Notes"
                        value={formData.Notes}
                        onChange={handleChange}
                    />
                </div>

                <div>
                    <label htmlFor="LAT">LAT:</label>
                    <input
                        type="text"
                        id="LAT"
                        name="LAT"
                        value={formData.LAT}
                        onChange={handleChange}
                    />
                </div>

                <div>
                    <label htmlFor="LNG">LNG:</label>
                    <input
                        type="text"
                        id="LNG"
                        name="LNG"
                        value={formData.LNG}
                        onChange={handleChange}
                    />
                </div>

                <button type="submit">Submit</button>
            </form>

            <div>
                <h1>Data Table</h1>
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Notes</th>
                            <th>LAT</th>
                            <th>LNG</th>
                            <th>del</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((item) => (
                            <tr key={item.ID}>
                                <td>{item.ID}</td>
                                <td>{item.Name}</td>
                                <td>{item.Notes}</td>
                                <td>{item.LAT}</td>
                                <td>{item.LNG}</td>
                                <td><button onClick={() => deleteItem(item.id)}>Delete</button></td>

                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Form;
