import "./styles.css";
import "leaflet/dist/leaflet.css";
import { useState, useRef, useMemo, useCallback, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";
import Form from './Form';
import { Icon, divIcon, point } from "leaflet";
import axios from 'axios';

const center = [31.951591711679697, 35.93934368582822];
const zoom = 19;

function DisplayPosition({ map }) {
    const [position, setPosition] = useState(() => map.getCenter());

    const onClick = useCallback(() => {
        map.setView(center, zoom);
    }, [map]);

    const onMove = useCallback(() => {
        setPosition(map.getCenter());
    }, [map]);

    useEffect(() => {
        map.on('move', onMove);
        return () => {
            map.off('move', onMove);
        };
    }, [map, onMove]);

    return (
        <p>
            latitude: {position.lat.toFixed(4)}, longitude: {position.lng.toFixed(4)}{' '}
            <button onClick={onClick}>reset</button>
        </p>
    );
}

export default function App1() {
    const [data, setData] = useState([]);
    const [markers, setMarkers] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:3001/maps');
                setData(response.data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, []); // Only run once when the component mounts

    useEffect(() => {
        const newMarkers = data.map((item) => ({
            geocode: [item.LAT, item.LNG],
            popUp: item.Notes, // Use item.Notes for popup content
        }));
        setMarkers(newMarkers);
    }, [data]);

    const customIcon = new Icon({
        iconUrl: require("./img/google-maps.png"),
        iconSize: [38, 38],
    });

    const [map, setMap] = useState(null);

    const displayMap = useMemo(() => (
        <MapContainer
            center={center}
            zoom={zoom}
            ref={setMap}
        >
            <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            <MarkerClusterGroup>
                {markers.map((marker, index) => (
                    <Marker key={index} position={marker.geocode} icon={customIcon}>
                        <Popup>{marker.popUp}</Popup>
                    </Marker>
                ))}
            </MarkerClusterGroup>
        </MapContainer>
    ), [markers]);

    return (
        <div className="suctionMap">
            {map ? <DisplayPosition map={map} /> : null}
            {displayMap}
            {/* <Form lat={position.lat.toFixed(4)} long={position.lng.toFixed(4)} /> */}
        </div>
    );
}
